			int index = random.Next(availableTopics.Count);
			string selectedTopic = availableTopics[index];

			// –î–æ–±–∞–≤–ª—è–µ–º –≤ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–Ω—ã–µ
			usedTopics.Add(selectedTopic);

